import { Component } from "react";
import './style.css'
import ListPekerjaan from "./ListPekerjaan";

class InputPekerjaan extends Component{
    state = {
        title: "",
        completed: true
    }

    oneChange = e => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const fromIsNotEmpty = this.state.title

        if(fromIsNotEmpty){
            const newData = {
                title: this.state.title
            }

            this.props.tambahPekerjaan(newData);
            this.setState({
                title: ""
            })
        }else{
            alert("isi data dlu")
        }
    }


    render(){
        const viewMode = {};
        const editMode = {};
    
        if (this.setState.completed){
            viewMode.textdecoration = "line-through";
        } else {
            editMode.textdecoration = "line-through";
        }    

        return(
            <div 
            className="InputText" >
                <h1>Daftar Pekerjaan </h1>
                <input type="text" placeholder="Tambah Pekerjaan..." value={this.state.title} name="title" onChange={this.oneChange}/>
                <button onClick={this.handleSubmit}>masukkan</button>


            </div>


        )
    }
}

export default InputPekerjaan
