import { v4 as uuidv4 } from "uuid";

export default function Home(){

    const [newTitle, setNewTitle] = useState("")
    const [todos, setTodos] = useState([
       {
               id:uuidv4(),
               title:"lorem 1",
               done: false
             },
             {
               id:uuidv4(),
               title:"lorem 2",
               done: true
             },
    ])

    const handleEdittodo = (id) => {
        setTodos(todos.map((todo) => todo.id === id ? ({...todo, done: !todo.done}) : todo))}

    const handleAddNewTodo = (event) => {
        event.preventDefault()
        const newTodo = {
            id:uuidv4(),
            title:newTitle,
            done:false
            }
            setTodos([newTodo, ...todos])
            setNewTitle("")
        }

    const handleHapusTodo = (id) => {
        const HapusListPekerja = todos.filter((item) => item.id !== id);      
            this.setTodos({ data: handleHapusTodo });
          }

    const render = todos.map((todo) => (
        <div key={todo.id}>
          <input checked={todo.done} type="checkbox" onChange={() => handleEdittodo(todo.id)}/> 
          {todo.title}
        <button onClick={handleHapusTodo}> hapus</button>

        </div>      
      ))

  return (
    <div>
        <input type="text" value={newTitle} onChange={(event) => setNewTitle(event.target.value)}/>
        <button onClick={handleAddNewTodo}>
          Tambah todo baru
        </button>
      {render}
    </div>

)
    
}
